files.content:
:root {
    --gold: #d4af37;
    --gold-light: #f8e9a1;
    --gold-dark: #a17c00;
    --black: #000000;
    --white: #FFFFFF;
    --gray: #F8F8F8;
    --gray-dark: #666666;
    --shadow-light: rgba(0, 0, 0, 0.1);
    --shadow-medium: rgba(0, 0, 0, 0.15);
    --shadow-dark: rgba(0, 0, 0, 0.25);
    --transition-fast: 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-medium: 0.5s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-slow: 0.8s cubic-bezier(0.4, 0, 0.2, 1);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Roboto', sans-serif;
    background-color: var(--white);
    overflow-x: hidden;
    line-height: 1.6;
}

/* Loading Animation */
.loader {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--white);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 9999;
    transition: opacity var(--transition-medium), visibility var(--transition-medium);
}

.loader.hide {
    opacity: 0;
    visibility: hidden;
}

.loader-spinner {
    width: 60px;
    height: 60px;
    border: 4px solid var(--gray);
    border-top-color: var(--gold);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Container */
.container {
    max-width: 1440px;
    margin: 0 auto;
    position: relative;
}

.content-wrapper {
    padding: 0 5%;
    transition: padding var(--transition-fast);
}

@media (min-width: 1200px) {
    .content-wrapper {
        padding: 0 100px;
    }
}

/* Navigation Header */
.nav-header {
    width: 100%;
    height: 70px;
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 5%;
    position: fixed;
    top: 0;
    z-index: 1000;
    box-shadow: 0 2px 20px var(--shadow-light);
    transition: all var(--transition-fast);
}

.nav-header.scrolled {
    height: 60px;
    box-shadow: 0 4px 30px var(--shadow-medium);
}

.logo {
    display: flex;
    align-items: center;
    gap: 12px;
    cursor: pointer;
    transition: transform var(--transition-fast);
}

.logo:hover {
    transform: scale(1.05);
}

.logo img {
    height: 45px;
    width: auto;
    filter: drop-shadow(0 2px 4px var(--shadow-light));
    transition: all var(--transition-fast);
}

.nav-header.scrolled .logo img {
    height: 40px;
}

.logo-text {
    color: var(--gold);
    font-size: 20px;
    font-weight: 700;
    text-shadow: 1px 1px 2px var(--shadow-light);
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 30px;
    align-items: center;
}

.nav-menu a {
    color: var(--black);
    text-decoration: none;
    font-size: 16px;
    font-weight: 500;
    position: relative;
    transition: color var(--transition-fast);
    padding: 5px 0;
}

.nav-menu a::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--gold);
    transition: width var(--transition-fast);
}

.nav-menu a:hover {
    color: var(--gold);
}

.nav-menu a:hover::after,
.nav-menu a.active::after {
    width: 100%;
}

.nav-menu a.active {
    color: var(--gold);
}

/* Gallery Page Header */
.header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    height: 50px;
    background-color: #000000;
    z-index: 1000;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 30px;
}

/* Hamburger Menu */
.hamburger {
    display: none;
    flex-direction: column;
    cursor: pointer;
    padding: 5px;
    z-index: 1001;
}

.hamburger span {
    width: 28px;
    height: 3px;
    background-color: var(--black);
    margin: 3px 0;
    transition: all var(--transition-fast);
    border-radius: 2px;
}

.gallery.html .hamburger span {
    background-color: #FFD700;
}

.hamburger.active span:nth-child(1) {
    transform: rotate(45deg) translate(5px, 5px);
}

.hamburger.active span:nth-child(2) {
    opacity: 0;
}

.hamburger.active span:nth-child(3) {
    transform: rotate(-45deg) translate(7px, -6px);
}

/* Mobile Menu */
.mobile-menu {
    position: fixed;
    top: 70px;
    left: 0;
    right: 0;
    background: rgba(255, 255, 255, 0.98);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    transform: translateY(-100%);
    transition: transform var(--transition-medium);
    box-shadow: 0 10px 30px var(--shadow-medium);
    max-height: calc(100vh - 70px);
    overflow-y: auto;
}

.mobile-menu.active {
    transform: translateY(0);
}

.mobile-menu ul {
    list-style: none;
    padding: 20px;
}

.mobile-menu li {
    margin: 15px 0;
    opacity: 0;
    transform: translateX(-20px);
    animation: slideIn 0.5s forwards;
}

.mobile-menu.active li:nth-child(1) { animation-delay: 0.1s; }
.mobile-menu.active li:nth-child(2) { animation-delay: 0.2s; }
.mobile-menu.active li:nth-child(3) { animation-delay: 0.3s; }
.mobile-menu.active li:nth-child(4) { animation-delay: 0.4s; }
.mobile-menu.active li:nth-child(5) { animation-delay: 0.5s; }
.mobile-menu.active li:nth-child(6) { animation-delay: 0.6s; }

@keyframes slideIn {
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

.mobile-menu a {
    color: var(--black);
    text-decoration: none;
    font-size: 18px;
    font-weight: 500;
    display: block;
    padding: 10px;
    transition: all var(--transition-fast);
    border-radius: 8px;
}

.mobile-menu a:hover {
    color: var(--gold);
    background: rgba(212, 175, 55, 0.1);
    padding-left: 20px;
}

/* Hero Section */
.hero {
    margin-top: 70px;
    height: 600px;
    background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.3)),
                url('https://builtbyward.store//uploads/OVERVIEW%20HOUSE/Gen4%20Create%20a%20breathtaking%20aerial%20photograph%20captured%20during%20golden%20hour,%20showcasing%20t%20a-2,%2016451831.png');
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-align: center;
    color: var(--white);
    position: relative;
    overflow: hidden;
}

.hero::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at center, transparent 0%, rgba(0, 0, 0, 0.3) 100%);
    pointer-events: none;
}

.hero-content {
    z-index: 1;
    animation: fadeInUp 1s ease-out;
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.hero-headline {
    margin-bottom: 25px;
    animation: heroTitle 1.2s ease-out;
}

@keyframes heroTitle {
    from {
        opacity: 0;
        transform: translateY(-20px) scale(0.9);
    }
    to {
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

.hero-headline .gold {
    color: var(--gold);
    font-size: clamp(36px, 8vw, 64px);
    font-weight: 900;
    text-shadow: 2px 4px 8px rgba(0, 0, 0, 0.5);
    letter-spacing: 2px;
}

.hero-headline .white {
    color: var(--white);
    font-size: clamp(28px, 6vw, 48px);
    font-weight: 700;
    display: block;
    text-shadow: 2px 3px 6px rgba(0, 0, 0, 0.5);
    margin-top: 10px;
}

.hero-tagline {
    font-size: clamp(16px, 3vw, 22px);
    margin-bottom: 30px;
    max-width: 700px;
    opacity: 0.95;
    text-shadow: 1px 2px 4px rgba(0, 0, 0, 0.5);
    animation: fadeIn 1.5s ease-out 0.5s both;
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 0.95; }
}

.cta-button {
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    color: var(--black);
    padding: 15px 40px;
    border: none;
    border-radius: 50px;
    font-size: 18px;
    font-weight: 700;
    cursor: pointer;
    transition: all var(--transition-fast);
    box-shadow: 0 4px 15px rgba(212, 175, 55, 0.4);
    position: relative;
    overflow: hidden;
    animation: fadeIn 1.5s ease-out 0.8s both;
}

.cta-button::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
}

.cta-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(212, 175, 55, 0.5);
}

.cta-button:hover::before {
    width: 300px;
    height: 300px;
}

.hero-stats {
    position: absolute;
    bottom: 40px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 50px;
    text-align: center;
    animation: slideUp 1s ease-out 1s both;
}

@keyframes slideUp {
    from {
        opacity: 0;
        transform: translateX(-50%) translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateX(-50%) translateY(0);
    }
}

.stat {
    font-size: 18px;
    font-weight: 500;
    text-shadow: 1px 2px 4px rgba(0, 0, 0, 0.5);
    padding: 10px 20px;
    background: rgba(212, 175, 55, 0.1);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(212, 175, 55, 0.3);
    border-radius: 30px;
    transition: all var(--transition-fast);
}

.stat:hover {
    background: rgba(212, 175, 55, 0.2);
    transform: translateY(-2px);
}

/* Section Styling */
section {
    position: relative;
    overflow: hidden;
}

.section-divider {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    overflow: hidden;
    line-height: 0;
}

.section-divider svg {
    position: relative;
    display: block;
    width: calc(100% + 1.3px);
    height: 60px;
}

/* About Section */
.about-section {
    padding: 100px 0;
    background: var(--white);
    position: relative;
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
}

.badge {
    display: inline-block;
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    color: var(--black);
    padding: 8px 20px;
    border-radius: 25px;
    font-size: 14px;
    font-weight: 600;
    margin-bottom: 20px;
    box-shadow: 0 2px 10px rgba(212, 175, 55, 0.3);
    animation: badgePulse 2s ease-in-out infinite;
}

@keyframes badgePulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

.section-title {
    font-size: clamp(28px, 5vw, 42px);
    font-weight: 800;
    color: var(--black);
    margin-bottom: 20px;
    position: relative;
    display: inline-block;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: var(--gold);
    border-radius: 2px;
}

.section-text {
    font-size: 18px;
    color: var(--gray-dark);
    line-height: 1.8;
    margin-bottom: 25px;
}

.learn-more {
    color: var(--gold);
    text-decoration: none;
    font-size: 18px;
    font-weight: 600;
    cursor: pointer;
    position: relative;
    display: inline-block;
    transition: all var(--transition-fast);
}

.learn-more::after {
    content: '‚Üí';
    margin-left: 5px;
    transition: transform var(--transition-fast);
}

.learn-more:hover {
    color: var(--gold-dark);
}

.learn-more:hover::after {
    transform: translateX(5px);
}

.about-images {
    position: relative;
    height: 500px;
}

.image-1 {
    position: absolute;
    width: 350px;
    height: 350px;
    background: linear-gradient(135deg, rgba(212, 175, 55, 0.1) 0%, rgba(212, 175, 55, 0.2) 100%),
                url('https://builtbyward.store//uploads/OVERVIEW%20HOUSE/Gen4%20Elegant%20luxury%20home%20in%20Highland%20Park%20Texas%20with%20brand%20new%20roof,%20gold%20s-terracotta,%20a-2,%204589674.png') center/cover;
    border-radius: 20px;
    top: 0;
    left: 0;
    box-shadow: 0 20px 40px var(--shadow-medium);
    transition: all var(--transition-medium);
}

.image-1:hover {
    transform: scale(1.05);
    box-shadow: 0 30px 60px var(--shadow-dark);
}

.image-2 {
    position: absolute;
    width: 320px;
    height: 320px;
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    border-radius: 20px;
    top: 80px;
    right: 0;
    transform: rotate(5deg);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--white);
    font-size: 32px;
    font-weight: 900;
    box-shadow: 0 20px 40px rgba(212, 175, 55, 0.3);
    transition: all var(--transition-medium);
}

.image-2:hover {
    transform: rotate(0deg) scale(1.05);
    box-shadow: 0 30px 60px rgba(212, 175, 55, 0.4);
}

/* Services Section */
.services-section {
    padding: 100px 0;
    background: linear-gradient(135deg, #f8f8f8 0%, #ffffff 100%);
    text-align: center;
    position: relative;
}

.services-title {
    font-size: clamp(24px, 4vw, 36px);
    font-weight: 800;
    color: var(--black);
    margin: 20px 0 60px;
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 40px;
    margin-top: 50px;
}

.service-card {
    padding: 40px 30px;
    border-radius: 25px;
    box-shadow: 0 15px 35px var(--shadow-light);
    transition: all var(--transition-medium);
    position: relative;
    overflow: hidden;
}

.service-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, transparent 0%, rgba(255, 255, 255, 0.1) 100%);
    opacity: 0;
    transition: opacity var(--transition-fast);
}

.service-card:hover {
    transform: translateY(-10px) scale(1.03);
    box-shadow: 0 25px 50px var(--shadow-medium);
}

.service-card:hover::before {
    opacity: 1;
}

.service-card.yellow {
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    color: var(--black);
}

.service-card.black {
    background: linear-gradient(135deg, #000000 0%, #333333 100%);
    color: var(--white);
}

.service-icon {
    font-size: 56px;
    margin-bottom: 25px;
    filter: drop-shadow(0 4px 8px var(--shadow-light));
    animation: iconFloat 3s ease-in-out infinite;
}

@keyframes iconFloat {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
}

.service-title {
    font-size: 24px;
    font-weight: 700;
    margin-bottom: 15px;
}

.service-text {
    font-size: 16px;
    line-height: 1.6;
    opacity: 0.9;
}

/* Mission & Vision Section */
.mission-vision-section {
    padding: 100px 0;
    background: var(--white);
}

.mission-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    margin-bottom: 60px;
    align-items: center;
}

.mission-text, .vision-text {
    min-height: 400px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 40px;
    background: rgba(248, 248, 248, 0.5);
    border-radius: 20px;
    border: 1px solid rgba(212, 175, 55, 0.1);
    transition: all var(--transition-medium);
}

.mission-text:hover, .vision-text:hover {
    background: rgba(248, 248, 248, 0.8);
    box-shadow: 0 10px 30px var(--shadow-light);
    transform: translateY(-5px);
}

.mission-text h3, .vision-text h3 {
    font-size: 32px;
    font-weight: 800;
    color: var(--black);
    margin-bottom: 25px;
    position: relative;
}

.mission-text h3::after, .vision-text h3::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 60px;
    height: 4px;
    background: var(--gold);
    border-radius: 2px;
}

.mission-text .section-text, .vision-text .section-text {
    font-size: 18px;
    color: var(--gray-dark);
    line-height: 1.8;
    flex-grow: 1;
    margin-bottom: 30px;
}

.future-vision-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: start;
}

.checklist {
    list-style: none;
    margin: 30px 0;
}

.checklist li {
    padding: 15px 0;
    padding-left: 40px;
    position: relative;
    font-size: 18px;
    color: var(--gray-dark);
    transition: all var(--transition-fast);
}

.checklist li:before {
    content: "‚úì";
    position: absolute;
    left: 0;
    color: var(--gold);
    font-weight: 700;
    font-size: 24px;
    animation: checkBounce 0.5s ease-out;
}

@keyframes checkBounce {
    0% { transform: scale(0); }
    50% { transform: scale(1.2); }
    100% { transform: scale(1); }
}

.checklist li:hover {
    color: var(--black);
    transform: translateX(5px);
}

.mission-image {
    width: 100%;
    height: 450px;
    background: rgba(224, 224, 224, 0.3);
    border-radius: 20px;
    position: relative;
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 20px 40px var(--shadow-light);
    transition: all var(--transition-medium);
}

.mission-image:hover {
    transform: scale(1.02);
    box-shadow: 0 30px 60px var(--shadow-medium);
}

.mission-image img {
    max-width: 100%;
    max-height: 100%;
    object-fit: cover;
    filter: drop-shadow(0 10px 20px var(--shadow-medium));
}

/* Reviews Section */
.reviews-section {
    padding: 80px 0;
    background: linear-gradient(135deg, #f8f8f8 0%, #ffffff 100%);
    position: relative;
}

.reviews-section::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, var(--gold), transparent);
}

.reviews-rating {
    margin-bottom: 50px;
}

.stars {
    font-size: 36px;
    margin-bottom: 10px;
    filter: drop-shadow(0 2px 4px var(--shadow-light));
    animation: starShine 3s ease-in-out infinite;
}

@keyframes starShine {
    0%, 100% { filter: drop-shadow(0 2px 4px var(--shadow-light)); }
    50% { filter: drop-shadow(0 0 20px rgba(255, 215, 0, 0.6)); }
}

.rating-text {
    font-size: 18px;
    color: var(--gray-dark);
    font-weight: 500;
}

.reviews-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 30px;
    margin-top: 50px;
}

.review-card {
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    padding: 35px;
    border-radius: 20px;
    box-shadow: 0 10px 30px var(--shadow-light);
    transition: all var(--transition-medium);
    border: 1px solid rgba(212, 175, 55, 0.1);
    position: relative;
    overflow: hidden;
}

.review-card::before {
    content: '';
    position: absolute;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(212, 175, 55, 0.1) 0%, transparent 70%);
    transform: rotate(45deg);
    transition: all var(--transition-medium);
    opacity: 0;
}

.review-card:hover {
    transform: translateY(-10px) scale(1.02);
    box-shadow: 0 20px 40px var(--shadow-medium);
}

.review-card:hover::before {
    opacity: 1;
}

.review-stars {
    font-size: 20px;
    margin-bottom: 15px;
    color: var(--gold);
}

.review-text {
    font-size: 16px;
    color: #333333;
    line-height: 1.8;
    margin-bottom: 25px;
    font-style: italic;
    position: relative;
    z-index: 1;
}

.reviewer {
    border-top: 1px solid rgba(0, 0, 0, 0.1);
    padding-top: 20px;
    display: flex;
    align-items: center;
    position: relative;
    z-index: 1;
}

.reviewer img,
.reviewer-img {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    object-fit: cover;
    margin-right: 15px;
    box-shadow: 0 4px 10px var(--shadow-light);
    transition: all var(--transition-fast);
}

.reviewer img:hover,
.reviewer-img:hover {
    transform: scale(1.1);
    box-shadow: 0 6px 15px var(--shadow-medium);
}

.reviewer strong {
    color: var(--black);
    font-size: 16px;
    font-weight: 600;
}

.review-date {
    color: #999999;
    font-size: 14px;
    display: block;
    margin-top: 3px;
}

/* Contact Section */
.contact-section {
    padding: 100px 0;
    background: linear-gradient(135deg, #f8f8f8 0%, #ffffff 100%);
    position: relative;
}

.contact-container {
    display: grid;
    grid-template-columns: 1fr 1.2fr;
    gap: 60px;
}

.contact-form-container {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    padding: 50px;
    border-radius: 25px;
    box-shadow: 0 20px 50px var(--shadow-medium);
    border: 1px solid rgba(212, 175, 55, 0.1);
    transition: all var(--transition-medium);
}

.contact-form-container:hover {
    box-shadow: 0 25px 60px var(--shadow-dark);
    transform: translateY(-5px);
}

.contact-form-container h3 {
    font-size: 32px;
    font-weight: 800;
    color: var(--black);
    margin-bottom: 40px;
    text-align: center;
}

.form-group {
    margin-bottom: 25px;
    position: relative;
}

.form-group label {
    display: block;
    font-size: 16px;
    color: var(--black);
    margin-bottom: 10px;
    font-weight: 600;
    transition: all var(--transition-fast);
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 15px 20px;
    border: 2px solid #E0E0E0;
    border-radius: 12px;
    font-size: 16px;
    font-family: 'Roboto', sans-serif;
    transition: all var(--transition-fast);
    background: rgba(255, 255, 255, 0.8);
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--gold);
    background: var(--white);
    box-shadow: 0 0 0 4px rgba(212, 175, 55, 0.1);
}

.form-group input:focus + label,
.form-group select:focus + label,
.form-group textarea:focus + label {
    color: var(--gold);
}

.form-group textarea {
    resize: vertical;
    min-height: 140px;
}

.form-row {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 25px;
}

.submit-button {
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    color: var(--black);
    padding: 18px 40px;
    border: none;
    border-radius: 50px;
    font-size: 18px;
    font-weight: 700;
    cursor: pointer;
    width: 100%;
    transition: all var(--transition-fast);
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.3);
    position: relative;
    overflow: hidden;
    margin-top: 10px;
}

.submit-button::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
}

.submit-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 40px rgba(212, 175, 55, 0.4);
}

.submit-button:hover::before {
    width: 400px;
    height: 400px;
}

.map-container {
    border-radius: 25px;
    overflow: hidden;
    box-shadow: 0 20px 50px var(--shadow-medium);
    height: 600px;
    transition: all var(--transition-medium);
    position: relative;
}

.map-container::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(to bottom, transparent 80%, rgba(0, 0, 0, 0.1) 100%);
    pointer-events: none;
}

.map-container:hover {
    transform: scale(1.02);
    box-shadow: 0 25px 60px var(--shadow-dark);
}

.map-container iframe {
    width: 100%;
    height: 100%;
    border: none;
}

.contact-info {
    background: linear-gradient(135deg, #000000 0%, #1a1a1a 100%);
    color: var(--white);
    padding: 40px;
    border-radius: 25px;
    margin-top: 30px;
    box-shadow: 0 15px 40px var(--shadow-medium);
    transition: all var(--transition-medium);
}

.contact-info:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 50px var(--shadow-dark);
}

.contact-info h4 {
    color: var(--gold);
    font-size: 24px;
    margin-bottom: 25px;
    font-weight: 700;
}

.contact-info p {
    font-size: 16px;
    margin-bottom: 15px;
    opacity: 0.9;
    transition: all var(--transition-fast);
}

.contact-info p:hover {
    opacity: 1;
    transform: translateX(5px);
}

/* Shingles Page Styles */
.shingle-hero {
    margin-top: 70px;
    height: 500px;
    background: linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.3)),
                url('https://builtbyward.store//uploads/SHINGLES/Photoshay_Brown_Shingle_Backdrop.jpg');
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-align: center;
    color: var(--white);
    position: relative;
    overflow: hidden;
}

.shingle-hero::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at center, transparent 0%, rgba(0, 0, 0, 0.3) 100%);
    pointer-events: none;
}

.shingle-hero h1 {
    color: var(--gold);
    font-size: clamp(36px, 8vw, 64px);
    font-weight: 900;
    margin-bottom: 25px;
    text-shadow: 2px 4px 8px rgba(0, 0, 0, 0.5);
    letter-spacing: 2px;
    animation: heroTitle 1.2s ease-out;
}

.shingle-hero p {
    font-size: clamp(16px, 3vw, 22px);
    max-width: 800px;
    line-height: 1.6;
    opacity: 0.95;
    text-shadow: 1px 2px 4px rgba(0, 0, 0, 0.5);
    animation: fadeIn 1.5s ease-out 0.5s both;
}

/* Shingles Section */
.shingles-section {
    padding: 80px 0;
    background: linear-gradient(135deg, #ffffff 0%, #f8f8f8 100%);
    position: relative;
}

.section-header {
    text-align: center;
    margin-bottom: 60px;
    animation: fadeIn 1s ease-out;
}

.section-header h2 {
    font-size: clamp(28px, 5vw, 42px);
    font-weight: 800;
    color: var(--gold);
    margin-bottom: 15px;
    position: relative;
    display: inline-block;
}

.section-header h2::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: var(--gold);
    border-radius: 2px;
}

.section-header p {
    font-size: 18px;
    color: var(--gray-dark);
    margin-top: 20px;
}

/* Category Filters */
.category-filters {
    display: flex;
    justify-content: center;
    gap: 15px;
    margin-bottom: 60px;
    flex-wrap: wrap;
    padding: 0 20px;
}

.filter-btn {
    padding: 12px 30px;
    border: 2px solid transparent;
    border-radius: 30px;
    font-size: 16px;
    font-weight: 500;
    cursor: pointer;
    transition: all var(--transition-fast);
    background: rgba(240, 240, 240, 0.8);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    color: #333333;
    position: relative;
    overflow: hidden;
}

.filter-btn::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: radial-gradient(circle, rgba(212, 175, 55, 0.3) 0%, transparent 70%);
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
}

.filter-btn.active {
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    color: var(--white);
    box-shadow: 0 4px 15px rgba(212, 175, 55, 0.4);
}

.filter-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 6px 20px var(--shadow-medium);
    border-color: var(--gold);
}

.filter-btn:hover::before {
    width: 300px;
    height: 300px;
}

/* Shingle Grid */
.shingle-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 40px;
    margin-bottom: 80px;
}

.shingle-card {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 10px 30px var(--shadow-light);
    transition: all var(--transition-medium);
    cursor: pointer;
    border: 1px solid rgba(212, 175, 55, 0.1);
    position: relative;
}

.shingle-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, transparent 0%, rgba(212, 175, 55, 0.05) 100%);
    opacity: 0;
    transition: opacity var(--transition-fast);
    z-index: 1;
}

.shingle-card:hover {
    transform: translateY(-10px) scale(1.02);
    box-shadow: 0 20px 50px var(--shadow-medium);
}

.shingle-card:hover::before {
    opacity: 1;
}

.shingle-image {
    height: 250px;
    position: relative;
    overflow: hidden;
    transition: all var(--transition-medium);
}

.heritage { 
    background: url('https://builtbyward.store//uploads/SHINGLES/TAMKO_Brown_Grey_Layered_Shingles.jpg') center/cover;
}
.prestige { 
    background: url('https://builtbyward.store//uploads/SHINGLES/Charcoal_Grey_Shingles.jpg') center/cover;
}
.artisan { 
    background: url('https://builtbyward.store//uploads/SHINGLES/TAMKO_Brown_Red_Rustic_Shingles.jpg') center/cover;
}
.storm { 
    background: url('https://builtbyward.store//uploads/SHINGLES/Dark-Grey_Black_Shingles.jpg') center/cover;
}
.eco { 
    background: url('https://builtbyward.store//uploads/SHINGLES/TAMKO_Brown_with_Blue_Grey_Accents_Shingles.jpg') center/cover;
}
.royal { 
    background: url('https://builtbyward.store//uploads/SHINGLES/Deep_Black_Shingles.jpg') center/cover;
}

.shingle-image::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(to bottom, transparent 60%, rgba(0, 0, 0, 0.3) 100%);
    opacity: 0;
    transition: opacity var(--transition-fast);
}

.shingle-card:hover .shingle-image::after {
    opacity: 1;
}

.shingle-content {
    padding: 35px;
    position: relative;
    z-index: 2;
}

.shingle-content h3 {
    font-size: 28px;
    font-weight: 700;
    color: var(--black);
    margin-bottom: 10px;
    transition: color var(--transition-fast);
}

.shingle-card:hover .shingle-content h3 {
    color: var(--gold);
}

.shingle-content .subtitle {
    font-size: 14px;
    color: var(--gold);
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-bottom: 20px;
    font-weight: 600;
}

.shingle-content p {
    font-size: 16px;
    color: var(--gray-dark);
    line-height: 1.8;
    margin-bottom: 25px;
}

.shingle-actions {
    display: flex;
    gap: 12px;
}

.btn-small {
    padding: 10px 24px;
    border: none;
    border-radius: 25px;
    font-size: 14px;
    font-weight: 600;
    cursor: pointer;
    transition: all var(--transition-fast);
    position: relative;
    overflow: hidden;
}

.btn-primary {
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    color: var(--white);
    box-shadow: 0 4px 15px rgba(212, 175, 55, 0.3);
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(212, 175, 55, 0.4);
}

.btn-secondary {
    background: rgba(240, 240, 240, 0.8);
    color: #333333;
    border: 1px solid rgba(212, 175, 55, 0.2);
}

.btn-secondary:hover {
    background: rgba(212, 175, 55, 0.1);
    border-color: var(--gold);
    color: var(--gold);
}

/* Feature Badges */
.feature-badges {
    background: linear-gradient(135deg, #f8f8f8 0%, #ffffff 100%);
    padding: 80px 0;
    text-align: center;
    position: relative;
}

.feature-badges::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, var(--gold), transparent);
}

.badges-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 50px;
    max-width: 1000px;
    margin: 0 auto;
}

.badge-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
    padding: 20px;
    transition: transform var(--transition-fast);
}

.badge-item:hover {
    transform: translateY(-5px);
}

.badge-icon {
    width: 90px;
    height: 90px;
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 40px;
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.3);
    transition: all var(--transition-fast);
    position: relative;
    overflow: hidden;
}

.badge-icon::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transform: rotate(45deg);
    transition: all 0.6s;
    opacity: 0;
}

.badge-item:hover .badge-icon {
    transform: scale(1.1);
    box-shadow: 0 15px 40px rgba(212, 175, 55, 0.4);
}

.badge-item:hover .badge-icon::before {
    animation: shine 0.6s ease-out;
}

@keyframes shine {
    0% {
        transform: translateX(-100%) translateY(-100%) rotate(45deg);
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    100% {
        transform: translateX(100%) translateY(100%) rotate(45deg);
        opacity: 0;
    }
}

.badge-item h4 {
    font-size: 22px;
    font-weight: 700;
    color: var(--black);
}

.badge-item p {
    font-size: 16px;
    color: var(--gray-dark);
}

/* CTA Section */
.cta-section {
    background: linear-gradient(135deg, #000000 0%, #1a1a1a 100%);
    color: var(--white);
    padding: 100px 0;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.cta-section::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(212, 175, 55, 0.1) 0%, transparent 50%);
    animation: rotate 30s linear infinite;
}

@keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

.cta-content {
    position: relative;
    z-index: 1;
}

.cta-content h2 {
    font-size: clamp(32px, 6vw, 48px);
    font-weight: 800;
    color: var(--gold);
    margin-bottom: 25px;
    text-shadow: 2px 4px 8px rgba(0, 0, 0, 0.3);
}

.cta-content p {
    font-size: 20px;
    margin-bottom: 40px;
    max-width: 700px;
    margin-left: auto;
    margin-right: auto;
    opacity: 0.9;
}

.cta-button {
    background: linear-gradient(135deg, var(--gold) 0%, var(--gold-light) 100%);
    color: var(--black);
    padding: 18px 50px;
    border: none;
    border-radius: 50px;
    font-size: 18px;
    font-weight: 700;
    cursor: pointer;
    transition: all var(--transition-fast);
    box-shadow: 0 10px 30px rgba(212, 175, 55, 0.4);
    position: relative;
    overflow: hidden;
    display: inline-block;
}

.cta-button::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
}

.cta-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 40px rgba(212, 175, 55, 0.5);
}

.cta-button:hover::before {
    width: 400px;
    height: 400px;
}

